let Joi,User;_941‍.x([["register",()=>register],["login",()=>login],["check",()=>check],["logout",()=>logout]]);_941‍.w("joi",[["default",["Joi"],function(v){Joi=v}]]);_941‍.w("../../models/user",[["default",["User"],function(v){User=v}]]);


       const register = async (ctx) => {
  const schema = Joi.object().keys({
    username: Joi.string()
      .alphanum()
      .min(3)
      .max(20)
      .required(),
    password: Joi.string().required(),
  });
  const result = Joi.validate(ctx.request.body, schema);
  if (result.error) {
    ctx.status = 400;
    ctx.body = result.error;
    return;
  }
  const { username, password } = ctx.request.body;
  try {
    const exists = await User.findByUsername(username);
    if (exists) {
      ctx.status = 409; //conflict
      return;
    }
    const user = new User({
      username,
    });
    await user.setPassword(password);
    await user.save();

    ctx.body = user.serialize();

    const token = user.generateToken();
    ctx.cookies.set('access_token', token, {
      maxAge: 1000 * 60 * 60 * 24 * 7, //expiredate: 7d
      httpOnly: true,
    });
  } catch (e) {
    ctx.throw(500, e);
  }
};

       const login = async (ctx) => {
  const { username, password } = ctx.request.body;
  if (!username || !password) {
    ctx.status = 401; //unauthorized
    return;
  }
  try {
    const user = await User.findByUsername(username);
    if (!user) {
      ctx.status = 401; //unauthorized
      return;
    }
    const valid = await user.checkPassword(password);
    if (!valid) {
      ctx.status = 401;
      return;
    }
    ctx.body = user.serialize();

    const token = user.generateToken();
    ctx.cookies.set('access_token', token, {
      maxAge: 1000 * 60 * 60 * 24 * 7, //expiredate: 7d
      httpOnly: true,
    });
  } catch (e) {
    ctx.throw(500, e);
  }
};
       const check = async (ctx) => {
  //check login status
  const { user } = ctx.state;
  if (!user) {
    ctx.status = 401; //Unauthorized
    return;
  }
  ctx.body = user;
};
       const logout = async (ctx) => {
  ctx.cookies.set('access_token');
  ctx.status = 204;
};
